head.script:
if (typeof String.prototype.startsWith != 'function') {
String.prototype.startsWith = function (str){
return this.indexOf(str) == 0;
};
}

$(document).ready(function() {
var path = window.location.pathname;
switch(path) {
case '/p/about-me.html':
$('#about-toggle').addClass('active-toggle');
break;
default:
if (path.startsWith('/search/label/C%23')) {
$('#c-sharp-toggle').addClass('active-toggle');
} else if (path.startsWith('/search/label/Projects')) {
$('#projects-toggle').addClass('active-toggle');
} else {
$('#home-toggle').addClass('active-toggle');
}
}
});
